var netcat = require('./index.js')
var NetcatClient = netcat.client

var cmd = '/bin/sh'

new NetcatClient()
 .addr('127.0.0.1')
 .port(6767)
 .retry(4000)
 .filter(filterShxCmd)
 .connect()
 .exec(cmd)

function filterShxCmd (chunk, enc, cb) {
  if (chunk.toString().indexOf('shx') === 0) {
    // TODO ..
    cb(null)
  } else cb(null, chunk)
}
